mapper.update:
update order_tbl
set product_id = #{productId,jdbcType=INTEGER},
    total_amount = #{totalAmount,jdbcType=INTEGER},
    status = #{status,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, product_id, total_amount, status
from order_tbl
where id = #{id,jdbcType=INTEGER}
